# sep 参数 设置间隔符
print("www", "runoob", "com", sep=".")

# end 参数 设置结尾
print("www", "runoob1", "com <next> ", end="")  # 空格结尾
print("www", "runoob2", "com", end="\n")  # 换行结尾

# flush 参数 强制刷新输出
import time

print("Loading", end="")
for i in range(5):
    print(".", end='', flush=True)
    # print(".",end = '',flush = False)  # 默认false: 只会等一行要打印的所有内容都有之后才会输出
    time.sleep(0.5)
print("Loaded")
